local script = "local Players = game:GetService(\"Players\")local UserInputService = game:GetService(\"UserInputService\")local function setSpeed(player, speed) local humanoid = player.Character:WaitForChild(\"Humanoid\") if humanoid then humanoid.WalkSpeed = speed end end;local function setJumpHeight(player, jumpPower) local humanoid = player.Character:WaitForChild(\"Humanoid\") if humanoid then humanoid.JumpPower = jumpPower end end;local function createGUI() local screenGui = Instance.new(\"ScreenGui\") screenGui.Name = \"SpeedJumpGUI\" screenGui.Parent = Players.LocalPlayer.PlayerGui local frame = Instance.new(\"Frame\") frame.Parent = screenGui frame.BackgroundColor3 = Color3.fromRGB(50, 50, 50) frame.Position = UDim2.new(0.5, -150, 0.5, -100) frame.Size = UDim2.new(0, 300, 0, 150) frame.BorderSizePixel = 0 frame.Draggable = true local speedLabel = Instance.new(\"TextLabel\") speedLabel.Parent = frame speedLabel.Text = \"Speed:\" speedLabel.TextColor3 = Color3.new(1, 1, 1) speedLabel.Position = UDim2.new(0.05, 0, 0.1, 0) local speedTextBox = Instance.new(\"TextBox\") speedTextBox.Parent = frame speedTextBox.Text = \"10\" speedTextBox.Position = UDim2.new(0.6, 0, 0.1, 0) speedTextBox.Size = UDim2.new(0.3, 0, 0, 25) local jumpLabel = Instance.new(\"TextLabel\") jumpLabel.Parent = frame jumpLabel.Text = \"Jump Power:\" jumpLabel.TextColor3 = Color3.new(1, 1, 1) jumpLabel.Position = UDim2.new(0.05, 0, 0.6, 0) local jumpTextBox = Instance.new(\"TextBox\") jumpTextBox.Parent = frame jumpTextBox.Text = \"50\" jumpTextBox.Position = UDim2.new(0.6, 0, 0.6, 0) jumpTextBox.Size = UDim2.new(0.3, 0, 0, 25) local applyButton = Instance.new(\"TextButton\") applyButton.Parent = frame applyButton.Text = \"Apply\" applyButton.BackgroundColor3 = Color3.fromRGB(100, 170, 250) applyButton.TextColor3 = Color3.new(1, 1, 1) applyButton.Position = UDim2.new(0.1,0, 0.8,0) applyButton.Size = UDim2.new(0.8, 0, 0.1,0) applyButton.MouseButton1Click:Connect(function() local speed = tonumber(speedTextBox.Text) local jumpPower = tonumber(jumpTextBox.Text) if speed then setSpeed(Players.LocalPlayer, speed) end if jumpPower then setJumpHeight(Players.LocalPlayer, jumpPower) end end) local hideButton = Instance.new(\"TextButton\") hideButton.Parent = frame hideButton.Text = \"Hide\" hideButton.BackgroundColor3 = Color3.fromRGB(250, 100, 100) hideButton.TextColor3 = Color3.new(1, 1, 1) hideButton.Position = UDim2.new(0.1, 0, 0.9,0) hideButton.Size = UDim2.new(0.8, 0, 0.1,0) hideButton.MouseButton1Click:Connect(function() screenGui:Destroy() end) end; createGUI()"
loadstring(script)()
